package beans;

public class Resultado {
	
	private boolean ok;
	private String motivo;
	
	public Resultado() {
		// TODO Auto-generated constructor stub
	}

	public boolean isOk() {
		return ok;
	}

	public void setOk(boolean ok) {
		this.ok = ok;
	}

	public String getMotivo() {
		return motivo;
	}

	public void setMotivo(String motivo) {
		this.motivo = motivo;
	}
	
	

}
